parameters.jsCode:
if ($input.item.json.body) {
  $input.item.json.content = $input.item.json.body
    // Remove <style>, <script>, and other head-like content blocks
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<!--[\s\S]*?-->/g, '') // HTML comments

    // Remove all HTML tags
    .replace(/<\/?[^<>]+?>/g, '')

    // Remove CSS or JS snippets (e.g., .class { ... } or function foo() { ... })
    .replace(/(?:.\[\w-]+|\#[\w-]+)?\s*\{[^}]*\}/g, '')  // CSS rules
    .replace(/\bfunction\s+\w+\s*\([^)]*\)\s*\{[^}]*\}/g, '') // JS functions

    // Remove UI junk and boilerplate phrases (commonly seen in web UIs)
    .replace(/\b(Subscribe|Book a (call|meeting)|Leave a Reply|Cancel Reply|Login|Logout|Signup|Your Name|Email|Website|Privacy Policy|Terms & Conditions|Close Menu|Skip to main content|Back to top|Search|Menu|Share|Tweet|Pin)\b/gi, '')

    // Remove social and footer keywords
    .replace(/\b(Facebook|Twitter|LinkedIn|Instagram|\¬©\s\d{4}[\w\s.]+)\b/gi, '')

    // Normalize multiple spaces and line breaks
    .replace(/\r?\n|\r/g, ' ')      // Remove all newlines
    .replace(/\s{2,}/g, ' ')        // Collapse multiple spaces
    .trim();                        // Trim leading/trailing whitespace
}

return $input.item;
